#!/usr/bin/env python3

import sys

test_seq = ''

#known AT content = 5/9 = 55%

countA = test_seq.count('A')
countT = test_seq.count('T')
countG = test_seq.count('G')
countC = test_seq.count('C')

countAT = int(countA) + int(countT)
countGC = int(countG) + int(countC)

contentAT = (countAT/len(test_seq)) * 100
contentGC = (countGC/len(test_seq)) * 100

print('The AT content for the sequence is:', contentAT, '%.\nThe GC content for the sequence is:', contentGC, '%.')

print('\nThis script has finished!\n\n')
