#!/usr/bin/env python3

import sys

seq = ''

seq_rev = seq[::-1]

#print(seq_rev)

seq_list = list(seq_rev)

seq_rcom = list()

for base in range(len(seq_list)):
	if seq_list[base] == 'A':
		seq_rcom.append('T')
	elif seq_list[base] == 'T':
		seq_rcom.append('A')
	elif seq_list[base] == 'G':
		seq_rcom.append('C')
	elif seq_list[base] == 'C':
		seq_rcom.append('G')

seq_rcom_str = ''.join(seq_rcom)

print('Original sequence:', seq)
#print('Reverse complement should read GGGCCCAAATTT.')
print('Reverse complement:', seq_rcom_str)		
		
