#!/usr/bin/env python3

mySeq = ''

uniqNT = list(set(mySeq))

myDict = {}

for NT in uniqNT:
	myDict[NT] = mySeq.count(NT)

countA = myDict['A']
countT = myDict['T']
countG = myDict['G']
countC = myDict['C']

print('A:', countA, '\nT:', countT, '\nG:', countG, '\nC:',  countC)

GCcontent = ((countG + countC)/len(mySeq))*100

print('GC content: {0:.2f} percent'.format(GCcontent))

